import axios from "axios";

const fetchFriendsData = async () => {
  const sub = getSubFromLoginToken();
  if (!sub) {
    setError("ÌÜ†ÌÅ∞ Ï†ïÎ≥¥Í∞Ä ÏóÜÏäµÎãàÎã§.");
    return;
  }

  console.log("üì° ÏÑúÎ≤ÑÎ°ú Î≥¥ÎÇº sub Í∞í:", sub);

  setLoading(true);
  setError(null);

  try {
    const response = await axios.post(
      "https://192.168.0.89:9999/api/getfriends",
      { sub },
      {
        headers: {
          "Content-Type": "application/json",
        },
        withCredentials: true, // ‚úÖ CORS Î∞è Ïù∏Ï¶ù Ï†ïÎ≥¥ Ìè¨Ìï®
      }
    );

    console.log("üì° ÏÑúÎ≤Ñ ÏùëÎãµ Îç∞Ïù¥ÌÑ∞:", response.data);
    setFriendsData(response.data);
  } catch (error) {
    console.error("‚ùå Îç∞Ïù¥ÌÑ∞ Í∞ÄÏ†∏Ïò§Í∏∞ Ïã§Ìå®:", error);
    setError(error.response?.data?.message || "Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
  } finally {
    setLoading(false);
  }
};
