import React, { useState } from "react";
import axios from "axios";
function getSubFromLoginToken() {
  const REST = process.env.REACT_APP_RESTURL;

  const token = localStorage.getItem("logintoken");
  if (!token) {
    console.warn("Î°úÍ∑∏Ïù∏ ÌÜ†ÌÅ∞Ïù¥ Ï°¥Ïû¨ÌïòÏßÄ ÏïäÏäµÎãàÎã§.");
    return "";
  }
  
  try {
    const parts = token.split('.');
    if (parts.length !== 3) {
      throw new Error("Ïú†Ìö®ÌïòÏßÄ ÏïäÏùÄ ÌÜ†ÌÅ∞ ÌòïÏãùÏûÖÎãàÎã§.");
    }
    const payload = parts[1];
    const decodedPayload = JSON.parse(atob(payload));
    return decodedPayload.sub || "";
  } catch (error) {
    console.error("ÌÜ†ÌÅ∞ ÌååÏã± Ï§ë Ïò§Î•ò Î∞úÏÉù:", error);
    return "";
  }
}

const AddFriend = ({ onClose }) => {
  const [friendCode, setFriendCode] = useState("");
  const [error, setError] = useState("");

  const handleChange = (e) => {
    const value = e.target.value;
    if (/^\d*$/.test(value)) {
      setFriendCode(value);
      setError("");
    } else {
      setError("Ïà´ÏûêÎßå ÏûÖÎ†• Í∞ÄÎä•Ìï©ÎãàÎã§.");
    }
  };

  const handleSubmit = () => {
    const sub = getSubFromLoginToken();
    if (!sub) {
      setError("Î°úÍ∑∏Ïù∏ Ï†ïÎ≥¥Í∞Ä ÏóÜÏäµÎãàÎã§.");
      return;
    }

    console.log("üì° ÏÑúÎ≤ÑÎ°ú Î≥¥ÎÇº Îç∞Ïù¥ÌÑ∞:", { sub, friendCode });

    const url = "https://172.16.17.63:9999/api/addfriend";

    fetch(url, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ sub, friendCode }),
    })
      .then((res) => {
        if (!res.ok) {
          throw new Error(`ÏÑúÎ≤Ñ ÏùëÎãµ ÏóêÎü¨: ${res.status}`);
        }
        return res.json();
      })
      .then((data) => {
        alert(data.message || "ÏπúÍµ¨ Ï∂îÍ∞Ä ÏÑ±Í≥µ!");
        onClose();
      })
      .catch((err) => {
        console.error("‚ùå ÏöîÏ≤≠ Ïò§Î•ò:", err);
        setError(err.message || "Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
      });
  };

  return (
    <div>
      <h2>ÏπúÍµ¨Ïùò Í∞úÏù∏ ÏãùÎ≥ÑÏΩîÎìúÎ•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî.</h2>
      <input
        type="text"
        value={friendCode}
        onChange={handleChange}
        placeholder="Ïà´ÏûêÎßå ÏûÖÎ†• Í∞ÄÎä•"
      />
      {error && <p>{error}</p>}
      <div>
        <button onClick={onClose}>Ï∑®ÏÜå</button>
        <button onClick={handleSubmit}>ÌôïÏù∏</button>
      </div>
    </div>
  );
};

export default AddFriend;
