import { useContext, useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { Context } from "../Connector";
import { Chat } from "../objects/chat";

export function ChatPage(props) {
  const { id } = useParams();
  const { sendMessage, sendRegister, Sub, setReceived, messageQueue, wsConnected } = useContext(Context);
  const [chats, setChats] = useState([]);
  const [msg, setMsg] = useState("");

  const sendHandle = () => {
    sendMessage(msg, id);
    setMsg("");
  };

  function setChat() {
    console.log("üí¨ Î©îÏãúÏßÄ ÏÑ∏ÌåÖ ÏãúÏûë");
    setChats([]);

    if (messageQueue[id] !== undefined) {
      setChats(messageQueue[id].slice().reverse());
      const top = document.querySelector(".OutputChat ul");
      if (top) {
        top.scrollTop = top.scrollHeight;
      }
    }
  }

  useEffect(() => {
    setChat();
  }, [messageQueue]);

  useEffect(() => {
    sendRegister(id);
    setReceived(id);
    setChat();
    console.log(id + "Î°ú Ïù¥ÎèôÌï®");
  }, [id, wsConnected]);

  const activeEnter = (e) => {
    if (e.key === "Enter") {
      sendHandle();
    }
  };

  const handleMsgInput = (event) => {
    setMsg(event.target.value);
  };

  return (
    <div className="Channel">
      <div className="OutputChat">
        <ul id="chatset">
          {chats.map((ele, idx) => {
            console.log("üêõ ele ÌôïÏù∏:", ele); // ‚úÖ Ïó¨Í∏∞ÏÑú ele Ï∂úÎ†•Îê®
            return (
              <Chat
                key={idx}
                name={ele.userName}
                text={ele.text}
                date={ele.date}
                userId={ele.userId}
                email={ele.email}
              />
            );
          })}
        </ul>
      </div>
      <div className="InputChat">
        <input
          value={msg}
          onChange={handleMsgInput}
          onKeyDown={(e) => activeEnter(e)}
        />
        <button onClick={sendHandle}>SEND</button>
      </div>
    </div>
  );
}
