import { useState } from "react";
import FriendButtonContainer from "../component/FriendButtonContainer";

const Me = () => {
  const [isFriendButtonActive, setIsFriendButtonActive] = useState(true); // 기본값 true → 처음부터 보임

  return (
    <div>
      {/* 친구 버튼 (눌렀을 때 FriendButtonContainer 보이게) */}
      <button onClick={() => setIsFriendButtonActive(true)}>친구</button>

      {/* FriendButtonContainer가 활성화된 경우만 렌더링 */}
      {isFriendButtonActive && (
        <FriendButtonContainer 
          isActive={isFriendButtonActive} 
          setIsActive={setIsFriendButtonActive} 
        />
      )}
    </div>
  );
};

export default Me;
