import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

export function FindId() {
    const [email, setEmail] = useState('');
    const [message, setMessage] = useState('');
    const navigate = useNavigate();
    const REST = process.env.REACT_APP_RESTURL; // 환경변수에서 API 주소 가져오기

    // 아이디 찾기 요청 함수
    const doFindId = async () => {
        try {
            const response = await axios.post(`https://${REST}/api/find-id`, { email });
            setMessage(response.data); // 서버 응답 메시지 표시
        } catch (error) {
            setMessage("아이디를 찾을 수 없습니다.");
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        doFindId(); // 아이디 찾기 실행
    };

    return (
        <div>
            <h2>아이디 찾기</h2>
            <form onSubmit={handleSubmit}>
                <label htmlFor="email">이메일</label>
                <input 
                    type="email" 
                    id="email" 
                    value={email} 
                    onChange={(e) => setEmail(e.target.value)} 
                    required 
                />
                <button type="submit">아이디 찾기</button>
            </form>
            {message && <p>{message}</p>}
        </div>
    );
}
