import React, { useState } from "react";
import Cookies from "js-cookie";

const AddFriend = ({ onClose }) => {
  const [friendCode, setFriendCode] = useState("");
  const [error, setError] = useState("");

  const handleChange = (e) => {
    const value = e.target.value;
    if (/^\d*$/.test(value)) {
      setFriendCode(value);
      setError("");
    } else {
      setError("숫자만 입력 가능합니다.");
    }
  };

  const handleSubmit = async () => {
    if (!friendCode) {
      setError("친구 코드 입력이 필요합니다.");
      return;
    }

    const token = Cookies.get("token");
    if (!token) {
      setError("로그인 정보가 없습니다.");
      return;
    }

    try {
      const payload = JSON.parse(atob(token.split(".")[1]));
      const sub = payload.sub;

      const response = await fetch("https://192.168.0.89/api/addfriend", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ sub: sub, friendCode: friendCode }),
      });

      const result = await response.json();

      if (!response.ok) {
        throw new Error(result.message || "친구 추가 실패");
      }

      alert("친구 추가 성공!");
      onClose();
    } catch (error) {
      setError(error.message || "오류가 발생했습니다.");
    }
  };

  return (
    <div>
      <h2>친구의 개인 식별코드를 입력하세요.</h2>
      <input
        type="text"
        value={friendCode}
        onChange={handleChange}
        placeholder="숫자만 입력 가능"
      />
      {error && <p>{error}</p>}
      <div>
        <button onClick={onClose}>취소</button>
        <button onClick={handleSubmit}>확인</button>
      </div>
    </div>
  );
};

export default AddFriend;
