import { useState, useRef, useEffect } from "react";
import { getSubFromLoginToken } from "../util/GetSubByLogintoken";

export function Chat(props) {
    const [showMenu, setShowMenu] = useState(false);
    const [isFriend, setIsFriend] = useState(false);
    const menuRef = useRef(null);

    const userId = props.userId; // 채팅창 유저의 identifyCode
    const loggedInUserId = getSubFromLoginToken(); // 로그인한 사용자의 identifyCode

    // 친구 여부 확인 api 호출
    useEffect(() => {
        if (userId && userId !== loggedInUserId) {
            fetch(`http://192.168.0.89:9999/api/isfriend?userId=${userId}&myId=${loggedInUserId}`)
                .then(response => response.json())
                .then(data => setIsFriend(data))
                .catch(error => console.error("친구 여부 확인 오류:", error));
        }
    }, [userId, loggedInUserId]);

    function handleContextMenu(event) {
        event.preventDefault(); // 기본적으로 기능하는 우클릭 메뉴 방지

        //자기 자신 체크
        if (String(userId) === String(loggedInUserId)) {
            return;
        }
        setShowMenu(true);
    }

    function closeMenu() {
        setShowMenu(false);
    }

    useEffect(() => {
        function handleClickOutside(event) {
            if (menuRef.current && !menuRef.current.contains(event.target)) {
                closeMenu();
            }
        }

        document.addEventListener("mousedown", handleClickOutside);
        return () => {
            document.removeEventListener("mousedown", handleClickOutside);
        };
    }, []);

    return (
        <>
            <li onContextMenu={handleContextMenu} className="chatmessage">
                <img src="https://cdn-icons-png.flaticon.com/512/6522/6522516.png" width={50} height={50} alt="Profile" />
                <b>{props.name}</b> : {props.text} <span className="date">{props.date}</span>
            </li>

            {showMenu && (
                <div ref={menuRef} className="context-menu">
                    <button onClick={() => alert("정보보기")}>정보보기</button>
                    {isFriend ? (
                        <>
                            <button onClick={() => alert("1:1 채팅하기")}>1:1 채팅</button>
                            <button onClick={() => alert("친구 삭제")}>친구 삭제</button>
                        </>
                    ) : (
                        <button onClick={() => alert(`친구 추가: ${props.userId}`)}>친구 추가</button>
                    )}
                </div>
            )}
        </>
    );
}
