import React, { useState } from "react";

const AddFriend = ({ onClose }) => {
  const [friendCode, setFriendCode] = useState("");
  const [error, setError] = useState("");

  const handleChange = (e) => {
    const value = e.target.value;
    if (/^\d*$/.test(value)) {
      setFriendCode(value);
      setError("");
    } else {
      setError("Ïà´ÏûêÎßå ÏûÖÎ†• Í∞ÄÎä•Ìï©ÎãàÎã§.");
    }
  };

  const handleSubmit = async () => {
    if (!friendCode) {
      setError("ÏπúÍµ¨ ÏΩîÎìú ÏûÖÎ†•Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
      console.log("‚ùå ÏπúÍµ¨ ÏΩîÎìúÍ∞Ä ÏûÖÎ†•ÎêòÏßÄ ÏïäÏùå");
      return;
    }
    
    const token = document.cookie
      .split("; ")
      .find((row) => row.startsWith("token="))
      ?.split("=")[1];
    
    if (!token) {
      setError("Î°úÍ∑∏Ïù∏ Ï†ïÎ≥¥Í∞Ä ÏóÜÏäµÎãàÎã§.");
      console.log("‚ùå ÌÜ†ÌÅ∞Ïù¥ ÏóÜÏùå");
      return;
    }
    
    try {
      const payload = JSON.parse(atob(token.split(".")[1]));
      const sub = payload.sub;

      console.log("üì° Î≥¥ÎÇ¥Îäî Îç∞Ïù¥ÌÑ∞:", { sub, friendCode });

      const response = await fetch("https://192.168.0.89/api/addfriend", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ sub: sub, friendCode: friendCode }),
      });

      console.log("üì° ÏùëÎãµ ÏÉÅÌÉú ÏΩîÎìú:", response.status);

      const result = await response.json().catch(() => ({})); // JSON Î≥ÄÌôò Ïã§Ìå® Ïãú Îπà Í∞ùÏ≤¥ Î∞òÌôò
      if (!response.ok) {
        console.error("‚ùå ÏÑúÎ≤Ñ ÏùëÎãµ Ïò§Î•ò:", result);
        throw new Error(result.message || "ÏπúÍµ¨ Ï∂îÍ∞Ä Ïã§Ìå®");
      }

      alert("‚úÖ ÏπúÍµ¨ Ï∂îÍ∞Ä ÏÑ±Í≥µ!");
      onClose();
    } catch (error) {
      console.error("‚ùå fetch Ïò§Î•ò:", error);
      setError(error.message || "Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
    }
  };

  return (
    <div>
      <h2>ÏπúÍµ¨Ïùò Í∞úÏù∏ ÏãùÎ≥ÑÏΩîÎìúÎ•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî.</h2>
      <input
        type="text"
        value={friendCode}
        onChange={handleChange}
        placeholder="Ïà´ÏûêÎßå ÏûÖÎ†• Í∞ÄÎä•"
      />
      {error && <p>{error}</p>}
      <div>
        <button onClick={onClose}>Ï∑®ÏÜå</button>
        <button onClick={handleSubmit}>ÌôïÏù∏</button>
      </div>
    </div>
  );
};

export default AddFriend;
