import { useState } from "react";

export function Chat(props) {
    const [showOptions, setShowOptions] = useState(false);

    function handleClick() {
        setShowOptions(!showOptions); // 클릭할 때마다 토글
    }

    return (
        <li onClick={handleClick} className="chatmessage">
            <img src="https://cdn-icons-png.flaticon.com/512/6522/6522516.png" width={50} height={50} alt="Profile" />
            <b>{props.name}</b> : {props.text} <span className="date">{props.date}</span>

            {/* 버튼 박스 */}
            {showOptions && (
                <div className="options-box">
                    <button onClick={() => alert("답장하기")}>답장</button>
                    <button onClick={() => alert("삭제하기")}>삭제</button>
                    <button onClick={() => alert("공유하기")}>공유</button>
                    <button onClick={() => setShowOptions(false)}>닫기</button>
                </div>
            )}
        </li>
    );
}
