import { Link, useLocation } from "react-router-dom";



 
export function ClickContainer(props){
    const location = useLocation();
    const pathSegments = location.pathname.split('/'); // 현재 경로를 분할
    pathSegments[pathSegments.length - 1] = props.channel; // 마지막 부분을 새 채널로 변경
    return(
        
        <div class="ClickCont">
            <Link to={`/channel/${pathSegments[1]}/${props.channel}`}>{props.name}</Link>
        </div>
    )
}
