import { useState, useRef, useEffect } from "react";
import { getSubFromLoginToken } from "../util/GetSubByLogintoken";

export function Chat(props) {
    const [showMenu, setShowMenu] = useState(false);
    const [menuPosition, setMenuPosition] = useState({ x: 0, y: 0 });
    const [isFriend, setIsFriend] = useState(null); // ÏπúÍµ¨ Ïó¨Î∂Ä (Ï¥àÍ∏∞Í∞í: null)
    const menuRef = useRef(null);

    const userId = props.userId; // Ï±ÑÌåÖÏ∞Ω Ïú†Ï†ÄÏùò identifyCode
    const loggedInUserId = getSubFromLoginToken(); // Î°úÍ∑∏Ïù∏Ìïú ÏÇ¨Ïö©ÏûêÏùò identifyCode

    console.log("üîç userId:", userId, "loggedInUserId:", loggedInUserId);

    // ‚úÖ ÏπúÍµ¨ Ïó¨Î∂Ä ÌôïÏù∏ API Ìò∏Ï∂ú (ÏûêÍ∏∞ ÏûêÏã†Ïù¥Î©¥ ÏöîÏ≤≠ Ïïà Î≥¥ÎÉÑ)
    useEffect(() => {
        if (!userId || !loggedInUserId) return; // ID Í∞íÏù¥ ÏóÜÏúºÎ©¥ Ïã§Ìñâ X
        if (String(userId) === String(loggedInUserId)) {
            console.log("‚ö†Ô∏è ÏûêÍ∏∞ ÏûêÏã†Ïù¥ÎØÄÎ°ú API ÏöîÏ≤≠ ÏÉùÎûµ");
            return;
        }

        console.log("üì° API ÏöîÏ≤≠ Î≥¥ÎÇ¥Îäî Ï§ë...");

        fetch(`https://192.168.0.89:9999/api/isfriend?userId=${userId}&myId=${loggedInUserId}`)
            .then(response => response.json()) // JSON Î≥ÄÌôò
            .then(data => {
                console.log("‚úÖ API ÏùëÎãµ Îç∞Ïù¥ÌÑ∞:", data);

                // üõ† ÏÑúÎ≤Ñ ÏùëÎãµÏù¥ { state: true } ÎòêÎäî { state: 1 } Ïùº Ïàò ÏûàÏúºÎØÄÎ°ú Ï≤¥ÌÅ¨
                if (data && (data.state === true || data.state === 1)) {
                    setIsFriend(true);
                } else if (data && (data.state === false || data.state === 0)) {
                    setIsFriend(false);
                } else {
                    console.error("‚ùå ÏòàÏÉÅÍ≥º Îã§Î•∏ ÏùëÎãµ ÌòïÏãù:", data);
                    setIsFriend(false); // Í∏∞Î≥∏Í∞í false ÏÑ§Ï†ï
                }
            })
            .catch(error => {
                console.error("‚ùå ÏπúÍµ¨ Ïó¨Î∂Ä ÌôïÏù∏ Ïò§Î•ò:", error);
                setIsFriend(false);
            });
    }, [userId, loggedInUserId]);

    function handleContextMenu(event) {
        event.preventDefault(); // Í∏∞Î≥∏Ï†ÅÏúºÎ°ú Í∏∞Îä•ÌïòÎäî Ïö∞ÌÅ¥Î¶≠ Î©îÎâ¥ Î∞©ÏßÄ

        // ‚úÖ ÏûêÍ∏∞ ÏûêÏã† Ï≤¥ÌÅ¨ (Ïö∞ÌÅ¥Î¶≠ Î©îÎâ¥ X)
        if (String(userId) === String(loggedInUserId)) {
            console.log("‚ö†Ô∏è ÏûêÍ∏∞ ÏûêÏã†Ïù¥ÎØÄÎ°ú Î©îÎâ¥ ÌëúÏãú Ïïà Ìï®");
            return;
        }

        setMenuPosition({ x: event.pageX, y: event.pageY });
        setShowMenu(true);
    }

    function closeMenu() {
        setShowMenu(false);
    }

    useEffect(() => {
        function handleClickOutside(event) {
            if (menuRef.current && !menuRef.current.contains(event.target)) {
                closeMenu();
            }
        }

        document.addEventListener("mousedown", handleClickOutside);
        return () => {
            document.removeEventListener("mousedown", handleClickOutside);
        };
    }, []);

    return (
        <>
            <li onContextMenu={handleContextMenu} className="chatmessage">
                <img src="https://cdn-icons-png.flaticon.com/512/6522/6522516.png" width={50} height={50} alt="Profile" />
                <b>{props.name}</b> : {props.text} <span className="date">{props.date}</span>
            </li>

            {showMenu && (
                <div ref={menuRef} className="context-menu">
                    <button onClick={() => alert("Ï†ïÎ≥¥Î≥¥Í∏∞")}>Ï†ïÎ≥¥Î≥¥Í∏∞</button>
                    {isFriend === true ? (
                        <>
                            <button onClick={() => alert("1:1 Ï±ÑÌåÖÌïòÍ∏∞")}>1:1 Ï±ÑÌåÖ</button>
                            <button onClick={() => alert("ÏπúÍµ¨ ÏÇ≠Ï†ú")}>ÏπúÍµ¨ ÏÇ≠Ï†ú</button>
                        </>
                    ) : isFriend === false ? (
                        <button onClick={() => alert(`ÏπúÍµ¨ Ï∂îÍ∞Ä: ${props.userId}`)}>ÏπúÍµ¨ Ï∂îÍ∞Ä</button>
                    ) : (
                        <button disabled>Î°úÎî© Ï§ë...</button> // API ÏùëÎãµ ÎåÄÍ∏∞ ÏÉÅÌÉú
                    )}
                </div>
            )}
        </>
    );
}
