import { useContext, useState } from "react";
import { useNavigate, useParams } from "react-router-dom"
import axios from "axios";
import "./Test.css"
axios.defaults.withCredentials = true;

export function Login(props){
    const REST = process.env.REACT_APP_RESTURL;

    const nav = useNavigate();
    const [id,setId] = useState("");
    const [pwd,setPwd] = useState("");
    const handleSubmit = (e) => {
        e.preventDefault();
        doLogin();
    }
    
    function doLogin(){
    //axiosÎ°ú Î°úÍ∑∏Ïù∏ ÏöîÏ≤≠, const trylogin = trueÏùºÎïå Ïã§Ìñâ X, response not okÎ©¥ Îã§Ïãú trylogin = false;, okÎ©¥ JWT ÏÉùÏÑ±ÌïòÍ≥† Î©îÏù∏ÏúºÎ°ú Ïù¥Îèô
        console.log(REST);
        console.log(id);
        console.log(pwd);
        console.log("doLogin");
        axios(`https://${REST}/api/login`, {
            method : "get",
            params : {
                id: id,
                pwd : pwd
            },
            credentials: "include", // üí° HTTP-Only Ïø†ÌÇ§ Ìè¨Ìï® ÏöîÏ≤≠
            withCredentials: true  // Ïø†ÌÇ§ Î∞è Ïù∏Ï¶ù Ìó§ÎçîÎ•º Ìè¨Ìï®ÌïòÏó¨ ÏöîÏ≤≠

        }).then((res) => {
                console.log(res);
                const token = res.headers['authorization'].split(' ')[1];
                console.log(token);
                nav("/");
        }).catch((err) => {
            
            console.log(err.response);
            alert("ÏïÑÏù¥ÎîîÍ∞Ä ÏïàÎê®! ");
        });
        
    }
    
    return(
        <div className="Channel">
        <div className="OutputChat">
            <form onSubmit={handleSubmit}>
                <input id="id" required  value = {id} onChange={(e) => setId(e.target.value)}></input> ID <p></p>
                <input id="pwd" required value ={pwd} onChange={(e) => setPwd(e.target.value)}></input> PWD <p></p>
                <button type="submit"> go </button>
            </form>
            <a href="/register" className="link-btn">ÌöåÏõêÍ∞ÄÏûÖ</a>
            <a href="/find-id" className="link-btn">ÏïÑÏù¥Îîî Ï∞æÍ∏∞</a>
            <a href="/find-password" className="link-btn">ÎπÑÎ∞ÄÎ≤àÌò∏ Ï∞æÍ∏∞</a>
        </div>
        
        </div>
    )
}
