const handleSubmit = async () => {
    if (!friendCode) {
      setError("ÏπúÍµ¨ ÏΩîÎìú ÏûÖÎ†•Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
      return;
    }
  
    let token = document.cookie
      .split("; ")
      .find((row) => row.startsWith("token="))
      ?.split("=")[1];
  
    if (!token) {
      token = localStorage.getItem("logintoken");
    }
  
    if (!token) {
      setError("Î°úÍ∑∏Ïù∏ Ï†ïÎ≥¥Í∞Ä ÏóÜÏäµÎãàÎã§.");
      console.log("‚ùå Î°úÍ∑∏Ïù∏ ÌÜ†ÌÅ∞Ïù¥ ÏóÜÏùå!");
      return;
    }
  
    try {
      const payload = JSON.parse(atob(token.split(".")[1]));
      const sub = Number(payload.sub); // ‚úÖ sub Í∞íÏùÑ Long(Number)ÏúºÎ°ú Î≥ÄÌôò
      const friendCodeNum = Number(friendCode); // ‚úÖ friendCode Í∞íÏùÑ Long(Number)ÏúºÎ°ú Î≥ÄÌôò
  
      console.log("üì° ÏÑúÎ≤ÑÎ°ú Î≥¥ÎÇ¥Îäî Îç∞Ïù¥ÌÑ∞:", { sub, friendCodeNum });
  
      const response = await axios.post("https://192.168.0.89/api/addfriend", {
        sub: sub,
        friendCode: friendCodeNum,
      }, {
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
          "Cache-Control": "no-cache, no-store, must-revalidate",
          Pragma: "no-cache",
          Expires: "0",
        },
        withCredentials: true, // ‚úÖ CORS Î∞è Ïù∏Ï¶ù Ï†ïÎ≥¥ Ìè¨Ìï®
      });
  
      console.log("üì° ÏÑúÎ≤Ñ ÏùëÎãµ ÏÉÅÌÉú:", response.status);
      console.log("üì° ÏÑúÎ≤Ñ ÏùëÎãµ Îç∞Ïù¥ÌÑ∞:", response.data);
  
      alert(response.data.message || "ÏπúÍµ¨ Ï∂îÍ∞Ä ÏÑ±Í≥µ!");
      onClose();
    } catch (error) {
      console.error("‚ùå ÏöîÏ≤≠ Ïò§Î•ò:", error);
      setError(error.response?.data?.message || "Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
    }
  };
