import { useState, useRef, useEffect } from "react";
import { getSubFromLoginToken } from "../util/GetSubByLogintoken";

export function Chat(props) {
    const [showMenu, setShowMenu] = useState(false);
    const [menuPosition, setMenuPosition] = useState({ x: 0, y: 0 });
    const [isFriend, setIsFriend] = useState(false);
    const menuRef = useRef(null);

    const userId = props.userId; // í˜„ìž¬ ì±„íŒ… ë©”ì‹œì§€ë¥¼ ë³´ë‚¸ ìœ ì € ID
    const loggedInUserId = getSubFromLoginToken(); // ë¡œê·¸ì¸í•œ ì‚¬ìš©ìžì˜ ID

    console.log("ðŸ” í˜„ìž¬ ë¡œê·¸ì¸í•œ ì‚¬ìš©ìž ID:", loggedInUserId);
    console.log("ðŸ“Œ ë©”ì‹œì§€ ìž‘ì„±ìž ID:", userId);

    // ì¹œêµ¬ ì—¬ë¶€ í™•ì¸ API í˜¸ì¶œ
    useEffect(() => {
        if (userId && userId !== loggedInUserId) {
            fetch(`http://192.168.0.89:9999/api/isfriend?userId=${userId}`)
                .then(response => response.json())
                .then(data => setIsFriend(data))
                .catch(error => console.error("ì¹œêµ¬ ì—¬ë¶€ í™•ì¸ ì˜¤ë¥˜:", error));
        }
    }, [userId, loggedInUserId]);

    function handleContextMenu(event) {
        event.preventDefault(); // ê¸°ë³¸ ìš°í´ë¦­ ë©”ë‰´ ë°©ì§€

        // âœ… ìžê¸° ìžì‹ ì¸ì§€ ì²´í¬í•˜ëŠ” ë¶€ë¶„
        if (String(userId) === String(loggedInUserId)) {
            console.log("âš ï¸ ìžê¸° ìžì‹ ì´ë¯€ë¡œ ìš°í´ë¦­ ë©”ë‰´ X");
            return;
        }

        setMenuPosition({ x: event.pageX, y: event.pageY });
        setShowMenu(true);
    }

    function closeMenu() {
        setShowMenu(false);
    }

    useEffect(() => {
        function handleClickOutside(event) {
            if (menuRef.current && !menuRef.current.contains(event.target)) {
                closeMenu();
            }
        }

        document.addEventListener("mousedown", handleClickOutside);
        return () => {
            document.removeEventListener("mousedown", handleClickOutside);
        };
    }, []);

    return (
        <>
            <li onContextMenu={handleContextMenu} className="chatmessage">
                <img src="https://cdn-icons-png.flaticon.com/512/6522/6522516.png" width={50} height={50} alt="Profile" />
                <b>{props.name}</b> : {props.text} <span className="date">{props.date}</span>
            </li>

            {showMenu && (
                <div ref={menuRef} className="context-menu">
                    <button onClick={() => alert("ì •ë³´ë³´ê¸°")}>ì •ë³´ë³´ê¸°</button>
                    {isFriend ? (
                        <>
                            <button onClick={() => alert("1:1 ì±„íŒ…í•˜ê¸°")}>1:1 ì±„íŒ…</button>
                            <button onClick={() => alert("ì¹œêµ¬ ì‚­ì œ")}>ì¹œêµ¬ ì‚­ì œ</button>
                        </>
                    ) : (
                        <button onClick={() => alert(`ì¹œêµ¬ ì¶”ê°€: ${props.userId}`)}>ì¹œêµ¬ ì¶”ê°€</button>
                    )}
                </div>
            )}
        </>
    );
}
