import React, { useState, useEffect } from "react";

function getSubFromLoginToken() {
  const token = localStorage.getItem("logintoken");
  if (!token) {
    console.warn("로그인 토큰이 존재하지 않습니다.");
    return "";
  }

  try {
    const parts = token.split(".");
    if (parts.length !== 3) {
      throw new Error("유효하지 않은 토큰 형식입니다.");
    }
    const payload = parts[1];
    const decodedPayload = JSON.parse(atob(payload));
    return decodedPayload.sub || "";
  } catch (error) {
    console.error("토큰 파싱 중 오류 발생:", error);
    return "";
  }
}

export function RequestFriends() {
  const REST = process.env.REACT_APP_RESTURL;

  const [friendsData, setFriendsData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [loadingActions, setLoadingActions] = useState({}); // ✅ 특정 버튼만 로딩

  const fetchFriendsData = () => {
    const sub = getSubFromLoginToken();
    if (!sub) {
      setError("토큰 정보가 없습니다.");
      return;
    }

    console.log("sub 값:", sub);

    setLoading(true);
    setError(null);
    const url = `https://${REST}/api/requestfriends`;

    fetch(url, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ sub }),
    })
      .then((res) => {
        if (!res.ok) {
          throw new Error(`서버 응답 에러: ${res.status}`);
        }
        return res.json();
      })
      .then((data) => {
        setFriendsData(data.length > 0 ? data : []); // 데이터가 있으면 저장, 없으면 빈 배열
        setLoading(false);
      })
      .catch((err) => {
        console.error("데이터 가져오기 실패:", err);
        setError(err.message);
        setLoading(false);
      });
  };

  useEffect(() => {
    fetchFriendsData(); // ✅ 최초 실행 시 목록 가져오기
  }, []);

  const handleFriendAction = (identifyCode, state) => {
    const sub = getSubFromLoginToken();
    if (!sub) {
      setError("토큰 정보가 없습니다.");
      return;
    }

    // 특정 identifyCode에 대한 로딩 상태 업데이트
    setLoadingActions((prev) => ({ ...prev, [identifyCode]: true }));

    const url = `https://${REST}/api/updatefriend`;
    fetch(url, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ sub, identifyCode, state }),
    })
      .then((res) => {
        if (!res.ok) {
          throw new Error(`서버 응답 에러: ${res.status}`);
        }
        return res.json();
      })
      .then(() => {
        fetchFriendsData(); // ✅ 요청 성공 후 친구 목록 갱신
      })
      .catch((err) => {
        console.error("친구 요청 처리 실패:", err);
        setError(err.message);
      })
      .finally(() => {
        setLoadingActions((prev) => ({ ...prev, [identifyCode]: false })); // 특정 버튼만 로딩 해제
      });
  };

  return (
    <div className="friends-container">
      <h3>친구요청 목록</h3>
      <button onClick={fetchFriendsData}>친구요청 목록 가져오기</button>

      {loading && <p>데이터 로딩 중...</p>}
      {error && <p>에러 발생: {error}</p>}

      {friendsData !== null && (
        friendsData.length > 0 ? (
          <ul>
            {friendsData.map((friend, index) => (
              <li key={index}>
                <strong>식별코드:</strong> {friend.identifyCode} |{" "}
                <strong>아이디:</strong> {friend.id} |{" "}
                <strong>닉네임:</strong> {friend.nickname}
                
                {/* 수락 및 거절 버튼 */}
                <button
                  onClick={() => handleFriendAction(friend.identifyCode, 1)}
                  disabled={loadingActions[friend.identifyCode]}
                >
                  {loadingActions[friend.identifyCode] ? "처리 중..." : "수락"}
                </button>

                <button
                  onClick={() => handleFriendAction(friend.identifyCode, -1)}
                  disabled={loadingActions[friend.identifyCode]}
                >
                  {loadingActions[friend.identifyCode] ? "처리 중..." : "거절"}
                </button>
              </li>
            ))}
          </ul>
        ) : (
          <p>친구 요청이 없습니다.</p>
        )
      )}
    </div>
  );
}
