import React, { useState } from "react";
import axios from "axios";

const AddFriend = ({ onClose }) => {
  const [friendCode, setFriendCode] = useState("");
  const [error, setError] = useState("");

  const handleChange = (e) => {
    const value = e.target.value;
    if (/^\d*$/.test(value)) {
      setFriendCode(value);
      setError("");
    } else {
      setError("Ïà´ÏûêÎßå ÏûÖÎ†• Í∞ÄÎä•Ìï©ÎãàÎã§.");
    }
  };

  const handleSubmit = async () => {
    if (!friendCode) {
      setError("ÏπúÍµ¨ ÏΩîÎìú ÏûÖÎ†•Ïù¥ ÌïÑÏöîÌï©ÎãàÎã§.");
      return;
    }
    
    let token = document.cookie
      .split("; ")
      .find((row) => row.startsWith("token="))
      ?.split("=")[1];
    
    if (!token) {
      token = localStorage.getItem("logintoken");
    }
    
    if (!token) {
      setError("Î°úÍ∑∏Ïù∏ Ï†ïÎ≥¥Í∞Ä ÏóÜÏäµÎãàÎã§.");
      return;
    }
    
    try {
      const payload = JSON.parse(atob(token.split(".")[1]));
      const sub = payload.sub;

      console.log("üì° Î≥¥ÎÇ¥Îäî Îç∞Ïù¥ÌÑ∞:", { sub, friendCode });

      const response = await axios.post("https://192.168.0.89/api/addfriend", 
        { sub: sub, friendCode: friendCode }, 
        {
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`,
          },
          withCredentials: true, // ‚úÖ CORS Î¨∏Ï†ú Ìï¥Í≤∞ÏùÑ ÏúÑÌïú ÏòµÏÖò Ï∂îÍ∞Ä
        }
      );

      console.log("üì° ÏùëÎãµ ÏÉÅÌÉú ÏΩîÎìú:", response.status);

      alert("‚úÖ ÏπúÍµ¨ Ï∂îÍ∞Ä ÏÑ±Í≥µ!");
      onClose();
    } catch (error) {
      console.error("‚ùå ÏöîÏ≤≠ Ïò§Î•ò:", error);
      setError(error.response?.data?.message || "Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
    }
  };

  return (
    <div>
      <h2>ÏπúÍµ¨Ïùò Í∞úÏù∏ ÏãùÎ≥ÑÏΩîÎìúÎ•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî.</h2>
      <input
        type="text"
        value={friendCode}
        onChange={handleChange}
        placeholder="Ïà´ÏûêÎßå ÏûÖÎ†• Í∞ÄÎä•"
      />
      {error && <p>{error}</p>}
      <div>
        <button onClick={onClose}>Ï∑®ÏÜå</button>
        <button onClick={handleSubmit}>ÌôïÏù∏</button>
      </div>
    </div>
  );
};

export default AddFriend;
