import React, { useState } from "react";
import axios from "axios";

const AddFriend = ({ onClose }) => {
  const [friendCode, setFriendCode] = useState("");
  const [error, setError] = useState("");

  const handleChange = (e) => {
    const value = e.target.value;
    if (/^\d*$/.test(value)) {
      setFriendCode(value);
      setError("");
    } else {
      setError("숫자만 입력 가능합니다.");
    }
  };

  const handleSubmit = async () => {
    if (!friendCode) {
      setError("친구 코드 입력이 필요합니다.");
      return;
    }

    let token = document.cookie
      .split("; ")
      .find((row) => row.startsWith("token="))
      ?.split("=")[1];

    if (!token) {
      token = localStorage.getItem("logintoken");
    }

    if (!token) {
      setError("로그인 정보가 없습니다.");
      return;
    }

    try {
      const payload = JSON.parse(atob(token.split(".")[1]));
      const sub = payload.sub;

      const response = await axios.post("https://192.168.0.89/api/addfriend", {
        sub: sub,
        friendCode: friendCode,
      }, {
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
          "Cache-Control": "no-cache, no-store, must-revalidate",
          Pragma: "no-cache",
          Expires: "0",
        },
        withCredentials: true, // ✅ CORS 및 인증 정보 포함
      });

      alert(response.data.message || "친구 추가 성공!");
      onClose();
    } catch (error) {
      console.error("❌ 요청 오류:", error);
      setError(error.response?.data?.message || "오류가 발생했습니다.");
    }
  };

  return (
    <div>
      <h2>친구의 개인 식별코드를 입력하세요.</h2>
      <input
        type="text"
        value={friendCode}
        onChange={handleChange}
        placeholder="숫자만 입력 가능"
      />
      {error && <p>{error}</p>}
      <div>
        <button onClick={onClose}>취소</button>
        <button onClick={handleSubmit}>확인</button>
      </div>
    </div>
  );
};

export default AddFriend;
