import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

export function FindId() {
    const [email, setEmail] = useState('');
    const [message, setMessage] = useState('');
    const navigate = useNavigate();
    const REST = process.env.REACT_APP_RESTURL; // API 주소 (환경 변수)

    // 아이디 찾기 요청 함수 (POST 방식)
    const doFindId = async () => {
        try {
            const response = await axios.post(`https://${REST}/api/find-id`, { email }, {
                headers: { 'Content-Type': 'application/json' }
            });

            // 서버 응답 처리
            if (response.status === 200) {
                setMessage(response.data); // 서버 응답 메시지 설정
            } else {
                setMessage("아이디를 찾을 수 없습니다.");
            }
        } catch (error) {
            console.error("아이디 찾기 오류:", error);
            setMessage("아이디를 찾을 수 없습니다.");
        }
    };

    // 폼 제출 시 실행
    const handleSubmit = (e) => {
        e.preventDefault(); // 기본 동작 방지
        doFindId(); // POST 요청 실행
    };

    return (
        <div>
            <h2>아이디 찾기</h2>
            <form onSubmit={handleSubmit}>
                <label htmlFor="email">이메일</label>
                <input 
                    type="email" 
                    id="email" 
                    value={email} 
                    onChange={(e) => setEmail(e.target.value)} 
                    required 
                />
                <button type="submit">아이디 찾기</button>
            </form>
            {message && <p>{message}</p>}
        </div>
    );
}
