import { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

axios.defaults.withCredentials = true;

export function Login(props) {
    const REST = process.env.REACT_APP_RESTURL;

    const nav = useNavigate();
    const [id, setId] = useState("");
    const [pwd, setPwd] = useState("");
    const handleSubmit = (e) => {
        e.preventDefault();
        doLogin();
    }

    function doLogin() {
        console.log(REST);
        console.log(id);
        console.log(pwd);
        console.log("doLogin");
        axios(`https://${REST}/api/login`, {
            method: "get",
            params: {
                id: id,
                pwd: pwd
            },
            credentials: "include", // üí° HTTP-Only Ïø†ÌÇ§ Ìè¨Ìï® ÏöîÏ≤≠
            withCredentials: true  // Ïø†ÌÇ§ Î∞è Ïù∏Ï¶ù Ìó§ÎçîÎ•º Ìè¨Ìï®ÌïòÏó¨ ÏöîÏ≤≠
        }).then((res) => {
            console.log(res);
            const token = res.headers['authorization'].split(' ')[1];
            console.log(token);
            nav("/");
        }).catch((err) => {
            console.log(err.response);
            alert("ÏïÑÏù¥ÎîîÎÇò ÎπÑÎ∞ÄÎ≤àÌò∏Í∞Ä ÏûòÎ™ªÎêòÏóàÏäµÎãàÎã§!");
        });
    }

    function reGisterform() {
        nav("/register");
    }

    function findCredentials() {
        nav("/find-credentials"); // ÏïÑÏù¥Îîî/ÎπÑÎ∞ÄÎ≤àÌò∏ Ï∞æÍ∏∞ ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô
    }

    return (
        <div className="login-container">
            <div className="login-form">
                <h2>Î°úÍ∑∏Ïù∏</h2>
                <form onSubmit={handleSubmit}>
                    <div className="input-group">
                        <input 
                            id="id" 
                            required  
                            value={id} 
                            onChange={(e) => setId(e.target.value)} 
                            placeholder="ÏïÑÏù¥Îîî" 
                        />
                    </div>
                    <div className="input-group">
                        <input 
                            id="pwd" 
                            required 
                            value={pwd} 
                            onChange={(e) => setPwd(e.target.value)} 
                            placeholder="ÎπÑÎ∞ÄÎ≤àÌò∏" 
                            type="password"
                        />
                    </div>
                    <button type="submit" className="submit-btn">Î°úÍ∑∏Ïù∏</button>
                </form>
                <div className="action-links">
                    <button type="button" onClick={reGisterform}>ÌöåÏõêÍ∞ÄÏûÖ</button>
                    <button type="button" onClick={findCredentials}>ÏïÑÏù¥Îîî/ÎπÑÎ∞ÄÎ≤àÌò∏ Ï∞æÍ∏∞</button>
                </div>
            </div>
        </div>
    )
}
