import { useState, useRef, useEffect } from "react";

const RightClickContainer = ({ 
    userId, 
    loggedInUserId, 
    isFriend, 
    addFriend, 
    deleteFriend,
    children,
    setHandleContextMenu // ✅ 함수 저장할 수 있도록 props 추가
}) => {
    const [showMenu, setShowMenu] = useState(false);
    const [menuPosition, setMenuPosition] = useState({ x: 0, y: 0 });
    const [selectedUserId, setSelectedUserId] = useState(null);
    const menuRef = useRef(null);

    // ✅ 우클릭 이벤트 핸들러
    function handleContextMenu(event, id) {
    console.log("우클릭 실행됨! id:", id, "loggedInUserId:", loggedInUserId);
    if (String(id) === String(loggedInUserId)) {
        console.log("자기 자신 클릭! 요청 막음");
        return;
    }

    event.preventDefault();
    setSelectedUserId(id);
    setMenuPosition({ x: event.pageX, y: event.pageY });
    setShowMenu(true);
}

    // ✅ `setHandleContextMenu`를 통해 외부에서 함수 사용 가능하게 설정
    useEffect(() => {
        if (setHandleContextMenu) {
            setHandleContextMenu(() => handleContextMenu);
        }
    }, [setHandleContextMenu]);

    // ✅ 메뉴 닫기
    function closeMenu() {
        setShowMenu(false);
        setSelectedUserId(null);
    }

    // ✅ 외부 클릭 시 메뉴 닫기
    useEffect(() => {
        function handleClickOutside(event) {
            if (menuRef.current && !menuRef.current.contains(event.target)) {
                closeMenu();
            }
        }
        document.addEventListener("mousedown", handleClickOutside);
        return () => {
            document.removeEventListener("mousedown", handleClickOutside);
        };
    }, []);

    return (
        <div style={{ display: "inline-block", width: "100%" }}>
            {children}

            {showMenu && (
                <div 
                    ref={menuRef} 
                    style={{ 
                        position: "absolute", 
                        top: menuPosition.y, 
                        left: menuPosition.x, 
                        background: "#fff", 
                        border: "1px solid #ccc", 
                        padding: "5px",
                        boxShadow: "2px 2px 5px rgba(0,0,0,0.2)"
                    }}
                >
                    <button onClick={() => alert("정보보기")}>정보보기</button>
                    {isFriend ? (
                        <>
                            <button onClick={() => alert("1:1 채팅하기")}>1:1 채팅</button>
                            <button onClick={async () => {
                                await deleteFriend(selectedUserId, loggedInUserId);
                                closeMenu();
                            }}>
                                친구 삭제
                            </button>
                        </>
                    ) : (
                        <button onClick={() => {
                            addFriend(selectedUserId);
                            closeMenu();
                        }}>
                            친구 추가
                        </button>
                    )}
                </div>
            )}
        </div>
    );
};

export default RightClickContainer;
