import { useState, useEffect } from "react";
import FriendsContainer from "./FriendsContainer";
import AddFriend from "./AddFriend";
import RequestFriends from "./RequestFriends";

const FriendButtonContainer = ({ isActive }) => {
  const [activeTab, setActiveTab] = useState("friends"); // 기본값: 친구 목록

  useEffect(() => {
    if (isActive) {
      setActiveTab("friends"); // isActive가 true면 기본적으로 friends 탭 유지
    }
  }, [isActive]);

  if (!isActive) return null; // 비활성화되면 아무것도 렌더링하지 않음

  return (
    <div>
      {/* 탭 버튼 */}
      <div>
        <button onClick={() => setActiveTab("friends")}>친구 목록</button>
        <button onClick={() => setActiveTab("add")}>친구 추가</button>
        <button onClick={() => setActiveTab("requests")}>요청 목록</button>
      </div>

      {/* 선택된 탭에 따라 렌더링 */}
      <div>
        {activeTab === "friends" && <FriendsContainer />}
        {activeTab === "add" && <AddFriend />}
        {activeTab === "requests" && <RequestFriends />}
      </div>
    </div>
  );
};

export default FriendButtonContainer;
