import { useState, useRef, useEffect } from "react";
import { getSubFromLoginToken } from "./util/GetSubByLogintoken";

export function Chat(props) {
    const { userName, text, date, identify_code } = props.data; // 객체 구조 분해 할당
    const [showMenu, setShowMenu] = useState(false);
    const [menuPosition, setMenuPosition] = useState({ x: 0, y: 0 });
    const menuRef = useRef(null);
    const userId = props.data.identify_code; // 사용자 ID
    const currentUserId = getSubFromLoginToken(); // 현재 로그인한 사용자의 ID

    function handleContextMenu(event) {
        if (userId === currentUserId) {
            return; // 본인의 메시지라면 컨텍스트 메뉴를 열지 않음
        }
        event.preventDefault();
        setMenuPosition({ x: event.clientX, y: event.clientY });
        setShowMenu(true);
    }

    function closeMenu() {
        setShowMenu(false);
    }

    useEffect(() => {
        function handleClickOutside(event) {
            if (menuRef.current && !menuRef.current.contains(event.target)) {
                closeMenu();
            }
        }

        document.addEventListener("mousedown", handleClickOutside);
        return () => {
            document.removeEventListener("mousedown", handleClickOutside);
        };
    }, []);

    return (
        <>
            <li onContextMenu={handleContextMenu} className="chatmessage">
                <img src="https://cdn-icons-png.flaticon.com/512/6522/6522516.png" width={50} height={50} alt="Profile" />
                <b>{userName}</b> : {text} <span className="date">{date}</span>
            </li>

            {showMenu && (
                <div 
                    ref={menuRef}
                    className="context-menu"
                    style={{ top: `${menuPosition.y}px`, left: `${menuPosition.x}px`, position: "absolute", background: "white", border: "1px solid #ccc", padding: "10px", boxShadow: "0px 0px 5px rgba(0,0,0,0.2)" }}
                >
                    <button onClick={() => alert(`답장하기: ${userName} (ID: ${JSON.stringify(identify_code)})`)}>답장</button>
                    <button onClick={() => alert(`삭제하기 (ID: ${JSON.stringify(identify_code)})`)}>삭제</button>
                    <button onClick={() => alert(`공유하기 (ID: ${JSON.stringify(identify_code)})`)}>공유</button>
                </div>
            )}
        </>
    );
}
