      const response = await fetch("https://192.168.0.89/api/addfriend", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
          "Cache-Control": "no-cache, no-store, must-revalidate",
          Pragma: "no-cache",
          Expires: "0",
        },
        body: JSON.stringify({ sub: sub, friendCode: friendCode }),
      });
  
      // ‚úÖ JSON ÏùëÎãµ ÏùΩÍ∏∞
