import React, { useState } from "react";
import addFriend from "../util/AddFriendBySub."; // ✅ 유틸 파일에서 가져오기

const AddFriend = ({ onClose, initialFriendCode }) => {
  const [friendCode, setFriendCode] = useState(initialFriendCode || "");
  const [error, setError] = useState("");

  const handleChange = (e) => {
    const value = e.target.value;
    if (/^\d*$/.test(value)) {
      setFriendCode(value);
      setError("");
    } else {
      setError("숫자만 입력 가능합니다.");
    }
  };

  const handleSubmit = () => {
    if (!friendCode) {
      setError("친구 코드를 입력하세요.");
      return;
    }

    addFriend(friendCode); // ✅ 분리된 함수 사용
    onClose();
  };

  return (
    <div>
      <h2>친구의 개인 식별코드를 입력하세요.</h2>
      <input
        type="text"
        value={friendCode}
        onChange={handleChange}
        placeholder="숫자만 입력 가능"
      />
      {error && <p>{error}</p>}
      <div>
        <button onClick={onClose}>취소</button>
        <button onClick={handleSubmit}>확인</button>
      </div>
    </div>
  );
};

export default AddFriend;
