import React, { useState } from "react";

function getSubFromLoginToken() {
  const token = localStorage.getItem("logintoken");
  if (!token) {
    console.warn("Î°úÍ∑∏Ïù∏ ÌÜ†ÌÅ∞Ïù¥ Ï°¥Ïû¨ÌïòÏßÄ ÏïäÏäµÎãàÎã§.");
    return "";
  }
  
  try {
    const parts = token.split('.');
    if (parts.length !== 3) {
      throw new Error("Ïú†Ìö®ÌïòÏßÄ ÏïäÏùÄ ÌÜ†ÌÅ∞ ÌòïÏãùÏûÖÎãàÎã§.");
    }
    const payload = parts[1];
    const decodedPayload = JSON.parse(atob(payload));
    return decodedPayload.sub || "";
  } catch (error) {
    console.error("ÌÜ†ÌÅ∞ ÌååÏã± Ï§ë Ïò§Î•ò Î∞úÏÉù:", error);
    return "";
  }
}

export function RequestFriends() {
  const REST = process.env.REACT_APP_RESTURL;

  const [friendsData, setFriendsData] = useState(null); // Ï≤òÏùåÏóêÎäî `null` (ÏïÑÎ¨¥Í≤ÉÎèÑ ÌëúÏãú X)
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const fetchFriendsData = () => {
    const sub = getSubFromLoginToken();
    if (!sub) {
      setError("ÌÜ†ÌÅ∞ Ï†ïÎ≥¥Í∞Ä ÏóÜÏäµÎãàÎã§.");
      return;
    }
  
    console.log("sub Í∞í:", sub); // sub Í∞í ÌôïÏù∏
  
    setLoading(true);
    setError(null);
    const url = `https://${REST}/api/requestfriends`;
  
    fetch(url, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ sub }), // JSONÏúºÎ°ú sub Í∞íÏùÑ Ï†ÑÏÜ°
    })
      .then((res) => {
        if (!res.ok) {
          throw new Error(`ÏÑúÎ≤Ñ ÏùëÎãµ ÏóêÎü¨: ${res.status}`);
        }
        return res.json();
      })
      .then((data) => {
        setFriendsData(data.length > 0 ? data : []); // Îç∞Ïù¥ÌÑ∞Í∞Ä ÏûàÏúºÎ©¥ Ï†ÄÏû•, ÏóÜÏúºÎ©¥ Îπà Î∞∞Ïó¥
        setLoading(false);
      })
      .catch((err) => {
        console.error("Îç∞Ïù¥ÌÑ∞ Í∞ÄÏ†∏Ïò§Í∏∞ Ïã§Ìå®:", err);
        setError(err.message);
        setLoading(false);
      });
  };

  return (
    <div className="friends-container">
      <h3>ÏπúÍµ¨ÏöîÏ≤≠ Î™©Î°ù</h3>
      <button onClick={fetchFriendsData}>ÏπúÍµ¨ÏöîÏ≤≠ Î™©Î°ù Í∞ÄÏ†∏Ïò§Í∏∞</button>
      
      {loading && <p>Îç∞Ïù¥ÌÑ∞ Î°úÎî© Ï§ë...</p>}
      {error && <p>ÏóêÎü¨ Î∞úÏÉù: {error}</p>}

      {/* üî• Î≤ÑÌäºÏùÑ ÎàÑÎ•¥Í∏∞ Ï†ÑÏóêÎäî ÏïÑÎ¨¥Í≤ÉÎèÑ ÌëúÏãúÎêòÏßÄ ÏïäÎèÑÎ°ù Î≥ÄÍ≤Ω */}
      {friendsData !== null && (
        friendsData.length > 0 ? (
          <ul>
            {friendsData.map((friend, index) => (
              <li key={index}>
                <strong>ÏïÑÏù¥Îîî:</strong> {friend.username} | <strong>ÎãâÎÑ§ÏûÑ:</strong> {friend.nickname}
              </li>
            ))}
          </ul>
        ) : (
          <p>ÏπúÍµ¨ ÏöîÏ≤≠Ïù¥ ÏóÜÏäµÎãàÎã§.</p> // ÏöîÏ≤≠ ÌõÑ, Î¶¨Ïä§Ìä∏Í∞Ä ÎπÑÏñ¥ÏûàÏùÑ ÎïåÎßå ÌëúÏãú
        )
      )}
    </div>
  );
}
